    id = computeId(addr);
    INSUnified rnsUnified = _rnsUnified;
    if (rnsUnified.ownerOf(id) != address(this)) {
      uint256 claimedId = _claimWithResolver(addr, address(_defaultResolver));
      if (claimedId != id) revert InvalidId();
